        INSERT [dbo].[Faqs] ([Id], [SectionId], [Question], [Html]) VALUES (9, 4, N'How do we enter a legal relationship with First Regisrars on Address Verification?', N'Simply contact us via email info@firstregistrarsnigeria.com indicating your interest. Afterward, a Service Level Agreement (SLA) will be signed')
